import React ,{useState} from "react";
import { StatusBar } from "expo-status-bar";
import { SafeAreaView, Text, View } from "react-native";
import { TailwindProvider } from "tailwindcss-react-native";
import { NavigationContainer } from "@react-navigation/native";
import { createNativeStackNavigator } from "@react-navigation/native-stack";
import HomeScreen from "./screens/HomeScreen";
import Discover from "./screens/Discover";
import ItemScreen from "./screens/ItemScreen";
import Map from "./screens/Map";
import Login from "./screens/Login";
import 'react-native-gesture-handler';
import 'regenerator-runtime/runtime';


import { BrowserRouter, Route,Link, redirect } from "react-router-dom";
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';

const Stack = createNativeStackNavigator();

export default function App() {
  // const commands =[
  //   {
  //   commands: ["Go to *","Open"],
  //   callback(Redirect) => setRedirectUrl(Redirect)
  //   }
  // ]

const commands =[
  {
    command:["Go to *", "Open *"],
    callback: (redirectPage) => setRedirectUrl(redirectPage),
  },
];

  const {transcipt} = useSpeechRecognition({commands});
  const {redirectUrl,setRedirectUrl}=(useState(""));

  const pages=["Discover", "Map"];
  const urls={
    Discover:"/Discover",
    Map: "/Map",
  };

  if (!SpeechRecognition.browserSupportsSpeechRecognition()) {
    return null
  }

  let Redirect ="";
  if(redirectUrl){
    if(pages.includes(redirectUrl)){
      Redirect = <Redirect to={urls[redirectUrl]} ></Redirect>
    }
    else
    {
      redirect= <p>Could not find page : {redirectUrl}</p>
    }
  }

  return (
    <>  
    <div>
      {/* <p id="transcript"> Transcript:{transcipt}</p>
      <button onClick={SpeechRecognition.startListening}>Start</button>
    </div> */}
    
   
    {/* <TailwindProvider>
      <NavigationContainer>
        <Stack.Navigator>
          <Stack.Screen name="Home" component={HomeScreen} />
          <Stack.Screen name="Discover" component={Discover} />
          <Stack.Screen name="ItemScreen" component={ItemScreen} />
          <Stack.Screen name="Map" component={Map}/>
          <Stack.Screen name="Login" component={Login}/>
        </Stack.Navigator>
      </NavigationContainer>
    </TailwindProvider> */}

<BrowserRouter>
        <div id="links">
          <Link to="/">HomeScreen</Link>
          <Link to="/blog">Discover</Link>
          <Link to="/blog/new">Map</Link>
          {/* <Link to="/contact">Contact</Link> */}
        </div>

        <Route path="/" exact component={HomeScreen} />
        <Route path="/home" component={Discover} />
        <Route path="/blog" exact component={Map} />
        {/* <Route path="/blog/new" component={NewBlogPostPage} />
        <Route path="/contact" component={ContactPage} /> */}
        
      </BrowserRouter>

      <p id="transcript">Transcript: {transcipt}</p>

      <button onClick={SpeechRecognition.startListening}>Start</button>
    </div>
</>
    
  );
}
