import {
  View,
  Text,
  SafeAreaView,
  Image,
  ImageBackground,
  TouchableOpacity,
} from "react-native";
import React, { useLayoutEffect } from "react";
import * as Animatable from "react-native-animatable";

import { Link, useNavigation } from "@react-navigation/native";
import { HeroImage } from "../assets";
import "../screens/homescreen.css"
import vid from "../assets/vid3.mp4";
// import "../index.html";
import { Container, Navbar } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import New from "./New";
import { BsFillPersonFill} from "react-icons/bs";


const HomeScreen = () => {
  const navigate = useNavigate();

  useLayoutEffect(() => {
    navigation.setOptions({
      headerShown: false,
    });
  }, []);

  return (

  
    <SafeAreaView className=" bg-beige flex-1 relative">

<nav className="main-nav">
        {/* 1st logo part  */}
        <div className="logo">
          <h2>
            <span>W</span>e
            <span>T</span>ravel
          </h2>
        </div> 

        {/* 2nd menu part  */}
        <div className="menu-link">
        <ul>

          <a className="home "  onClick={() => navigate("HomeScreen")} href="#HomeScreen">Home</a>   

          <a className="discover" onClick={() => navigate("Discover")}  href="#Discover">Discover</a>           
                        
          <a className="map" onClick={() => navigate("Map")} href="#Map">Map</a>           
           
          <a className="contact" onClick={()=> navigation.navigate("Contact")} href="#Contact">Contact</a>           
          

          <ul className="icon">
            {/* <NavLink to="/New"><BsFillPersonFill></BsFillPersonFill></NavLink> */}
            
              <a className="icon" onClick={() => navigation.navigate("New")} href="#New"><BsFillPersonFill></BsFillPersonFill>
                </a>            
          </ul>          
        </ul>
        </div> 
      
      </nav>

      {/* First Section */}
      {/* <div className="App">
        <video autoPlay loop muted >
          <source src={vid} type="video/mp4"/>
        </video>

      </div> */}

      {/* <View className="flex-row px-6 mt-8 items-center space-x-2">
        <View className="w-16 h-16 bg-black rounded-full items-center justify-center">
          <Text className="text-[#00BCC9] text-3xl font-semibold">We</Text>
        </View>

        <Text className="text-[#2A2B4B] text-3xl font-semibold">Travel</Text>
      </View> */}

      {/* Second Section */}
      <View className="h1">
    
          <h3 className="head3">ADVENTURE IS WORTHWHILE</h3>

        <h2 className="head4">
        Discover New Places With Us, Adventure Awaits !
        </h2>
      </View>

      {/* Circle Section */}
      <View className="w-[400px] h-[400px] bg-[#00BCC9] rounded-full absolute bottom-36 -right-36 "></View>
      <View className="w-[400px] h-[400px] bg-[#E99265] rounded-full absolute -bottom-28 -left-36 position-absolute"></View>

    </SafeAreaView>
    
  );
};

export default HomeScreen;
