import '../screens/App.css'
import '../screens/New_sign.css'

import { useState, useEffect } from 'react';
import alanBtn from '@alan-ai/alan-sdk-web';
function New_sign(){

        const [name, setName] = useState('');
        const [phone, setPhone] = useState('')
        const [address, setAddress] = useState('');
        const ALAN_Key = '51006685f74dba7c95292ed5ff8657242e956eca572e1d8b807a3e2338fdd0dc/stage'
        useEffect(() => {
          alanBtn({
              key: ALAN_Key,
              onCommand: (commandData) => {
                if(commandData.command === 'name'){
                  setName(commandData.data)
                }
                if(commandData.command === 'phone'){
                  setPhone(commandData.data)
                }
                if(commandData.command === 'address'){
                  setAddress(commandData.data)
                }
              }
          });
        });
        return (
          <div className='form-fields'>
            <input
              placeholder='Name'
              name='name'
              onChange={(event) => setName(event.target.value)}
              value={name}
              type='text'
            />
            <input
              placeholder='Phone'
              name='phone'
              onChange={(event) => setPhone(event.target.value)}
              value={phone}
              type='text'
            />
            <input
              placeholder='Address'
              name='address'
              onChange={(event) => setAddress(event.target.value)}
              value={address}
              type='text'
            />

            <button className='submit-btn'>Submit</button>
          </div>
        );
      }


